import React from "react";
import "./Contact.css";
import { useState, useEffect } from "react";
import MessageIcon from '@material-ui/icons/Message';
import contact_us_image from './contact_us_page.jpg'

function Contact() {
  const [input_values , set_input_Values] =  useState({
    email : '' ,
    password : ''
})
const[errors , setErrors] = useState({})

const handleInputChange = e =>{
    const{name , value} = e.target
    set_input_Values({
        ...input_values ,
        [name]:value
    })
}
const handleSubmit = e =>{
    e.preventDefault() ;
}
  return (


    <div className="contact_page">
      <div className="contact_page_container1">
      <img className = "contact_us_img" src = {contact_us_image} alt=""/>
         <div className="contact_page_container1_title">Lorem, ipsum dolor.</div>
         <div className="contact_page_container1_description">Lorem ipsum dolor sit amet consectetur adipisicing elit. Ipsam, cum!</div>
      </div>
      <div className="contact_page_container2">
        <div className="contact_page_container2_left">
          <span className="contact_page_container2_left_title">
            <span className="text">Send us a message</span>
            <span className="icon"><MessageIcon/></span>
          </span>

          <form action="" onSubmit={handleSubmit}>
                <div className="form__email">
                    <input type="email" name="email" id="email" placeholder="Enter email" value={input_values.email} onChange={handleInputChange} required />
                </div>
                <div className="form__password">
                    <input type="password" name="password" id="password" placeholder="Enter password" value={input_values.password} onChange={handleInputChange} required />
                </div>
                <button type="submit" onSubmit>Login</button>
            </form>

        </div>
        <div className="contact_page_container2_right"></div>
              
      </div>

    </div>
  );
}

export default Contact;
